import React, { useContext, useState } from 'react'
import { GlobalState } from '../../../GlobalState'
import ApostaItem from '../utils/apostaItem'
import Loading from '../utils/loading/Loading'
import axios from 'axios'
import LoadMore from './LoadMore'


function Home() {
    const state = useContext(GlobalState)
    const [apostar, setApostar] = state.apostaAPI.apostar
    const [isAdmin] = state.userAPI.isAdmin
    const [loading, setLoading] = useState(false)

    const handleCheck = (id) => {
        apostar.forEach(aposta => {
            if (aposta._id === id) aposta.checked = !aposta.checked
        })
        setApostar([...apostar])
    }


    if (loading) return <div><Loading /></div>
    return (
        <>
            <div className="container-fluid">

                <div className="row">

                    <h2 className="mx-auto" style={{ color: '#fbc514', marginTop: '20px' }}>Apostas da Semana</h2>

                    <div className="img-banner">
                        {
                            apostar.map(aposta => {
                                return <ApostaItem key={aposta._id} aposta={aposta} />
                            })
                        }
                    </div>
                </div>

                <LoadMore /> {apostar.length === 0 && <Loading />}
            </div>

        </>
    )
}

export default Home
